import React from 'react';

import { useSelector, useDispatch} from 'react-redux';

import{remove,increment,decrement,clear,checkout,getCart} from '../../../slices/cartSlice';

export const Cart = (
    {}

    )=> {
        const cartList = useSelector(getCart);
        const dispatch = useDispatch();
        let count =0;
        return (
            <div>
                {cartList.map((cartItem,index ) =>(

                    <div key={index}>
                        <img style={{width:200, height:100}} src ={cartItem.item.product_images[0]}></img>
                        <div>{cartItem.item.quantity}</div>
                        <input type="number" id="quantity" name="quantity" min="0" max="5" value={cartItem.quantity}></input>
                        {count,count++} {cartItem.item.product_name}
                    </div>
                ))}
            </div>
        );
    };
export default Cart;
